package day1

import (
	"testing"
)

func Test_GetFloorForSanta_ResultShouldBe0(t *testing.T) {
	result := GetFloorForSanta("(())")
	if result != 0 {
		t.Fatalf(`getFloorForSanta("(())") = %v, want %v`, result, 0)
	}

	result = GetFloorForSanta("()()")
	if result != 0 {
		t.Fatalf(`getFloorForSanta("()()") = %v, want %v`, result, 0)
	}
}

func Test_GetFloorForSanta_ResultShouldBe3(t *testing.T) {
	result := GetFloorForSanta("(((")
	if result != 3 {
		t.Fatalf(`getFloorForSanta("(((") = %v, want %v`, result, 3)
	}

	result = GetFloorForSanta("(()(()(")
	if result != 3 {
		t.Fatalf(`getFloorForSanta("(()(()(") = %v, want %v`, result, 3)
	}

	result = GetFloorForSanta("))(((((")
	if result != 3 {
		t.Fatalf(`getFloorForSanta("))(((((") = %v, want %v`, result, 3)
	}
}

func Test_GetFloorForSanta_ResultShouldBeMinus1(t *testing.T) {
	result := GetFloorForSanta("())")
	if result != -1 {
		t.Fatalf(`getFloorForSanta("())") = %v, want %v`, result, -1)
	}

	result = GetFloorForSanta("))(")
	if result != -1 {
		t.Fatalf(`getFloorForSanta("))(") = %v, want %v`, result, -1)
	}
}

func Test_GetFloorForSanta_ResultShouldBeMinus3(t *testing.T) {
	result := GetFloorForSanta(")))")
	if result != -3 {
		t.Fatalf(`getFloorForSanta(")))") = %v, want %v`, result, -3)
	}

	result = GetFloorForSanta(")())())")
	if result != -3 {
		t.Fatalf(`getFloorForSanta(")())())") = %v, want %v`, result, -3)
	}
}

